export function getSpaceObjectInfo(spaceObject) {
    // console.log(spaceObject);
    let regEx = /`${spaceObject}`/i;
    const spaceObjectData = nasaData.filter(obj => {
        return regEx.test(obj.explanation)
    })
    return spaceObjectData;
}